&#125;

.prev-next .left-badge &#123;
	border-radius: 8px 0 8px 0;
&#125;

.prev-next .right-badge &#123;
	border-radius: 0 8px 0 8px;
	right: 10px;
&#125;

.paging &#123;
	margin-bottom: 15px;
&#125;

.paging .row &#123;
